
import { pluck } from 'utils';

import {
    FETCH_PHONES_SUCCESS
} from 'redux/actionTypes';

const initialState = {
    ids: []
};

export default (state=initialState, { type, payload }) => {
    switch (type) {
        case FETCH_PHONES_SUCCESS:
            const ids = pluck('id', payload);
            return{
                ...state,
                ids: ids
            }
        default:
            return state;
    }
}

