export const indexBy = (attr, data) => {
    const obj = {};
    for(let key of data){
        obj[key[attr]] = key;
    }
    return obj;
}

export const take = (txt, cntSymbol) => {
    let short = txt;
    if(txt.length > cntSymbol){
        short = short.slice(0, cntSymbol);
    }
    return `${short}...`;
};

export const prop = (attr, data) => {
    // console.log(data)
    return data[attr] ? data[attr] : null;
};

export const pluck = (attr, data) => {
    return data.map(el => el[attr]);
};

export const each = (arr, callback, thisArg) => {
    // console.log('thisArg', thisArg);
    for (let i = 0; i < arr.length; i++) {
        callback.call(thisArg, arr[i], i, arr);
    }
};

export const map = (arr, callback, thisArg) => {
    const result = [];
    for (let i = 0; i < arr.length; i++) {
        result.push(callback.call(thisArg, arr[i], i, arr));
    }
    return result;
};